//step02: get values from properties and set to project values.
def usePropertyFileFlag = context.expand( '${#Project#usePropertyFileFlag}' )
if (usePropertyFileFlag.equalsIgnoreCase("true")){
    props = new java.util.Properties()
    fis = new FileInputStream ("d:\\zipcode.txt")
    props.load (fis)
    testRunner.testCase.testSuite.project.setPropertyValue( "zip", props.getProperty ("zipCode") )
    testRunner.testCase.testSuite.project.setPropertyValue( "city", props.getProperty("city") )
}
